create procedure actualizarReproduccionesDisco(idDisco int )
begin
    update disco
		set numReproduccion=(select sum(numReproducciones) 
        from contenido where idCD=idDisco)
		where id=idDisco;
end;
//

create procedure actualizarReproduccionesGrupo(varidGrupo int )
begin
    update grupo
		set numReproduccion=(select sum(numReproducciones) 
			from disco join contenido 
            on disco.id = contenido.idCD 
			where idGrupo = varidGrupo)
		where grupo.id = varidGrupo;
end;
//

create procedure actualizarReproduccionesSerie(varIdSerie int )
begin
	update serie
		set numReproduccion=(select sum(numReproducciones) 
        from contenido where idSerie=varIdSerie)
		where id=varIdSerie;
end;
//

create procedure actualizarReproduccionesPodcaster(varidSerie int )
begin
	update podcaster
		set numReproduccion=(select sum(numReproducciones) 
			from serie join contenido 
			on serie.id = contenido.idSerie 
			where idPodcaster = varidSerie)
		where podcaster.id = varidSerie;
end;

