CREATE TRIGGER actualizarNumeroReproducciones AFTER UPDATE ON contenido FOR EACH ROW
begin
	declare varTipoContenido varchar(8);
    declare varIdDiscoOSerieAActualizar int;
    declare varIdGrupoOPodcasterAActualizar int;
    select tipoContenido into varTipoContenido from contenido where id = new.id;
		if varTipoContenido = "cancion" then
			set varIdDiscoOSerieAActualizar = (select contenido.idCD from contenido where id = new.id);
            set varIdGrupoOPodcasterAActualizar = (select disco.idGrupo from disco where id = varIdDiscoOSerieAActualizar);
            call actualizarReproduccionesDisco(varIdDiscoOSerieAActualizar);
			call actualizarReproduccionesGrupo(varIdGrupoOPodcasterAActualizar);
		else
			set varIdDiscoOSerieAActualizar = (select contenido.idSerie from contenido where id = new.id);
            set varIdGrupoOPodcasterAActualizar = (select serie.idPodcaster from serie where id = varIdDiscoOSerieAActualizar);
            call actualizarReproduccionesSerie(varIdDiscoOSerieAActualizar);
			call actualizarReproduccionesPodcaster(varIdGrupoOPodcasterAActualizar);
		end if;
end;
